mapper.select:
SELECT * FROM MEMBER_TBL
WHERE NAME = #{name}
AND EMAIL = #{email}

SELECT * FROM MEMBER_TBL
WHERE MEMBER_ID = #{memberId}
AND EMAIL = #{email}

SELECT COUNT(*)
FROM MEMBER_TBL
WHERE MEMBER_ID = #{memberId}

SELECT COUNT(*)
FROM MEMBER_TBL
WHERE EMAIL = #{email}

SELECT SNS_ID
FROM SNS_INFO_TBL
WHERE EMAIL = #{email}
AND SNS_TYPE = #{snsType}

mapper.update:
UPDATE MEMBER_TBL

     MEMBER_PW = #{memberPw}, 
     PHONE = #{phone} 

WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER_TBL
SET DELETE_YN = 'Y'
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER_TBL
SET MEMBER_PW = #{memberPw}
WHERE MEMBER_ID = #{memberId}

UPDATE PROFILE_IMG_TBL
SET FILE_NAME = #{fileName}, FILE_RENAME = #{fileRename}, FILE_PATH = #{filePath}
WHERE MEMBER_ID = #{memberId}
