mapper.select:
SELECT * FROM ( SELECT m.*, ROWNUM AS RN FROM ( SELECT m.MOVIE_NO, m.TITLE, m.POSTER_URL, m.RELEASE_DATE, m.RATING, m.SCREENING_STATUS, NVL(r.RESERVATION_RATE, 0) AS RESERVATION_RATE, NVL(rev.REVIEW_COUNT, 0) AS REVIEW_COUNT FROM MOVIE_TBL m LEFT JOIN ( SELECT MOVIE_NO, ROUND((VISITOR_COUNT / NULLIF(TOTAL_COUNT, 0)) * 100, 2) AS RESERVATION_RATE FROM ( SELECT m.MOVIE_NO, SUM(NVL(r.RESERVATION_VISITOR, 0)) AS VISITOR_COUNT, SUM(SUM(NVL(r.RESERVATION_VISITOR, 0))) OVER () AS TOTAL_COUNT FROM MOVIE_TBL m LEFT JOIN RESERVATION_TBL r ON m.MOVIE_NO = r.MOVIE_NO WHERE m.IS_BOOKABLE = 'Y' GROUP BY m.MOVIE_NO ) ) r ON m.MOVIE_NO = r.MOVIE_NO LEFT JOIN ( SELECT MOVIE_NO, COUNT(*) AS REVIEW_COUNT FROM REVIEW_TBL GROUP BY MOVIE_NO ) rev ON m.MOVIE_NO = rev.MOVIE_NO WHERE m.SCREENING_STATUS = #{status} ORDER BY
rev.REVIEW_COUNT DESC NULLS LAST
m.RELEASE_DATE ASC NULLS LAST
r.RESERVATION_RATE DESC NULLS LAST
) m WHERE ROWNUM <= #{offset} + #{size} ) WHERE RN > #{offset}

mapper.delete:
DELETE FROM REVIEW_TBL
WHERE REVIEW_NO = #{reviewNo}
  AND MEMBER_ID = #{memberId}
